/**
 * Problem:
 * We have to create an function that returns if the input array
 * follows the a 'Geometric' sequence, or a 'Arithmetic' sequence or no pattern. 
 * The input array can only contains no negative numbers
 * 
 * Output:
 * 'Arithmetic', 'Geometric', -1 when the array is doesn't follow the described patterns
 * 
 * 
 * What is an Arithmetic sequence?
 * Each number of this sequence is generated by the previous number plus a constant
 * 
 * What is a Geometric sequence?
 * Each number of this sequence is generated by the previous number multiplied by a constant named factor
 */


const mathSequences = (seq) => {

    let result = 'Arithmetic'

    const arithFactor = seq[1]-seq[0]
    const geoFactor = seq[1]/seq[0]

    // checking if the sequence is an  Aritmetic sequence. 
    let ariSeqAccumulator = seq[0]
    for(let item of seq) {
        if (ariSeqAccumulator === item ) {
            ariSeqAccumulator = ariSeqAccumulator + arithFactor
        } else {
            result = 'Goemetric'
            break
        }
    }
     if (result === 'Arithmetic')
        return result
    // checking if the sequence is a Goemtric sequence
    let geoSeqAccumulator = seq[0]
    for (let item of seq) { 
        if(geoSeqAccumulator === item) {
            geoSeqAccumulator = geoSeqAccumulator * geoFactor
        } else {
            result = -1
            break
        }

    }
    
    return result

}


