const mathSequencesv3 = (seq) => {
    let arith = new Set()
    let geo = new Set()
    for (let i =1; i<seq.length; i++) {
        let f1 = seq[i] - seq[i-1]
        let f2 = seq[i] / seq[i-1]
        arith.add(f1)
        geo.add(f2)
    }

    console.log(seq , arith)
    console.log(seq,geo)
    console.log('************')
    if (arith.size === 1) {
        return 'Arithmetic'
    }
    if (geo.size === 1) {
        return 'Geometric'
    }
    return -1
}

